import tkinter
import random
import math
from tkinter import *
openGL = Tk()
gl33 = Canvas(openGL, width=800, height=800)
gl33.pack()

gl33.create_rectangle(0, 0, 800, 800, fill="black")
seed = ""

a = int(math.sqrt(round(len(seed))))
y = 0
x = 0
at = 0
steps = 30

def row():
    for i in range(a):
        global x
        global y
        global at
        global steps
        if(seed[at] == "0"):
            gl33.create_rectangle(x, y, x+steps, y+steps, fill="black", outline="")
        elif(seed[at] == "1"):
            gl33.create_rectangle(x, y, x+steps, y+steps, fill="orange", outline="")
        x = x+steps
        at = at+1

for i in range(a):
    row()
    y = y+steps
    x = 0
            

    
